const glob = require('glob');

module.exports = function(grunt, options){

    const config = grunt.config.get('config');

    const ngAnnotate = {
        options: {
            singleQuotes: true
        },
        main: {
            files: {}
        }
    };

    const jsEntry = glob.sync(`${config.tools.path.build}/**/*.js`);
    
    jsEntry.map((filePah) => ngAnnotate.main.files[filePah] = filePah);

    return ngAnnotate;
};