module.exports = function(grunt, options) {

    // 동적 clean task
    grunt.registerTask('_clean', function(){

        var subTasks = this.args.join(':');

        const taskName = 'clean';

        const tasks = [`${taskName}:${subTasks}`];

        grunt.config.set(taskName, require(`../dynamic_config/${taskName}`)(grunt, subTasks));

        grunt.task.run(tasks);
    });
};