module.exports = function(grunt, options) {

    const gruntConfig = grunt.config;

    grunt.loadNpmTasks('grunt-html-build');
    grunt.loadNpmTasks('grunt-contrib-watch');

    // 환경별 설정을 merge 한다.
    gruntConfig.merge({
        config: {
            'env': 'dev'
        }
    });

    grunt.registerTask('serve', ['dev', 'browserSync', 'watch']);
};