
const _ = require('lodash');
const path = require('path');
const glob = require('glob');

const cssLintRcPath = path.join(__dirname, 'src/.csslintrc');

const originRules = glob.sync(__dirname + '/src/rules/**/*.js');

const retRules = {};

_.map(originRules, function(v, k){

    var ruleId = v.substr(v.lastIndexOf('/') + 1).replace('.js', '');

    retRules[ruleId] = {
        ruleId: ruleId,
        rulePath: v
    };
});

module.exports = {
    csslintrc: cssLintRcPath,
    rules: retRules
};
