
const _ = require('lodash');
const glob = require('glob');

const rules = {};
const rulePaths = glob.sync(__dirname + '/src/rules/*.js');

_.map(rulePaths, function(v, k){

    var ruleId = v.substr(v.lastIndexOf('/') + 1).replace('.js', '');
    rules[ruleId] = require(v);
});

module.exports = {
    rules: rules,
    environments: require('./src/config/env'),
    configs: {
        bxuipAngular: {
            plugins: [
                'bxuipAngular'
            ],
            rules: {
                'bxuipAngular/controller-name': 'off',
                'bxuipAngular/directive-name': 'off',
                'bxuipAngular/factory-name': 'off',
                'bxuipAngular/module-name': 'off',
                'bxuipAngular/provider-name': 'off',
                'bxuipAngular/service-name': 'off',
                'bxuipAngular/lab-service-name': 'off'
            }
        }
    }
};
