
const _ = require('lodash');
const glob = require('glob');

const util = require('./src/lib/util');

const retRules = {};

const originCustomRules = glob.sync(__dirname + '/src/rules/**/*.js');
const retCustomRules = {};

_.map(originCustomRules, function(v, k){

    var ruleId = v.substr(v.lastIndexOf('/') + 1).replace('.js', '');

    retRules[ruleId] = false;

    retCustomRules[ruleId] = {
        ruleId: ruleId,
        rulePath: util.getPath(v)
    };
});

module.exports = {
    rules: retRules,
    customRules: retCustomRules
};
