const clc = require('cli-color');
const os = require('os');
const _ = require('lodash');

module.exports = {
	getMessage: function(v){

	    if (!_.isPlainObject(v)) return this;

		var ret = ['\n'];

        _.map(v, function(v, k){
            ret.push(clc.white(k) + ': ' + clc.yellow(v));
        });

        return ret.join('\n');
	},
    getPath: function(v){

        if (!_.isString(v)) return this;

        if (this.isWindows()){
            if (v.lastIndexOf('/') > -1){
                v = v.split('/').join('\\');
            }
        }

        return v;
    },
    isWindows: function(){
        return os.platform() === 'win32' ? true : false;
    }
};